mapper.select:
select

from mmall_cart
where user_id = #{userId}
and product_id = #{productId}

mapper.delete:
delete from mmall_cart
where user_id = #{userId}

  and product_id in

    #{item}

mapper.update:
update mmall_cart
set checked = #{isSelected},
update_time = now()
where user_id = #{userId}

  and product_id = #{productId}
